import React,{useEffect} from 'react';
import CustomerTable from './CustomerTable/CustomerTable'
import * as actions from '../../../store/actions/index'
import {connect} from 'react-redux'
const AdminGetCustomers=(props)=> {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])
    const activateHandler=(id)=>{
        props.onActivate(id)
    }
    const deactivateHandler=(id)=>{
        props.onDeActivate(id)
    }
    return (
        <React.Fragment>
        {props.sellers!==null?
            props.sellers.map((seller,index) => {
            return (<tr className={styles.B}>
                <td className={styles.A}>{seller.id}</td>
                <td className={styles.A}>{seller.firstName}</td>
                <td className={styles.A}>{seller.middleName}</td>
                <td className={styles.A}>{seller.lastName}</td>
                <td className={styles.A}>{seller.username}</td>
                <td className={styles.A}>{seller.companyName}</td>
                <td className={styles.A}>{seller.companyContact}</td>
                <td className={styles.A}>{seller.addressDTO.city}</td>
                <td className={styles.A}>{seller.addressDTO.state}</td>
                <td className={styles.A}>{seller.addressDTO.country}</td>
                <td className={styles.A}>{seller.addressDTO.zipcode}</td>
                <td className={styles.A}>{seller.addressDTO.addressLine}</td>
                {seller.active?
                <td className={styles.A}>true</td>:
                <td className={styles.A}>false</td>}
                {seller.active?
                <td className={styles.A}>
                <button className={styles.Button1} onClick={()=>props.onDeActivate(seller.id)}>DeActivate</button></td>:
                <td className={styles.A}>
                <button className={styles.Button1} onClick={()=>props.onActivate(seller.id)}>Activate</button></td>
                }                
            </tr>)
        })
        :null}
        </React.Fragment>
    )
}
const mapStateToProps = state=>{
    console.log(state.admin.customers)
    return{

        customers:state.admin.customers
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCustomers()),
        onActivate:(id)=>dispatch(actions.activateCustomer(id)),
        onDeActivate:(id)=>dispatch(actions.deactivateCustomer(id))
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(AdminGetCustomers)
