    <Switch>
    <Route path="/" component={Welcome} />
    <Route path="/login" exact component={Login} />
    <Redirect to="/" />
  </Switch>
  );

  return (
    <div>
     <Layout>
       <Suspense fallback={<p>Loading....</p>}>
       {routes}
       </Suspense>
     </Layout>
    </div>
  );
}

