import React,{useEffect} from 'react'
import CustomerProfileTable from './CustomerProfileTable'
import classes from './customerProfileTable.module.css';
import {connect} from 'react-redux';
import * as actions from '../../store/actions/index'
import One from '../../assets/images/1.jpg'
const CustomerProfile=(props)=>{
    const {onClicked}=props
    useEffect(()=>{
        console.log('customer profile',props.history)
        onClicked()
    },[onClicked])

    const handlerr = ()=>{
        props.history.push("/updateProfile")
    }
    const changePasswordHandler=()=>{
        props.history.push("/changePassword")
    }
    return (
        <>
        <div>
            <div className={classes.Card}>
            <div>
                <center>                       
           <img className={classes.Imgs} src={`http://localhost:8080/users/${props.profile.fileName}`} alt="MyPic" width="200" height="200"/>
           </center>
           </div>
        </div>
        {props.profile!==null?

        <CustomerProfileTable 
        firstName={props.profile.firstName}
        middleName={props.profile.firstName}
        lastName={props.profile.lastName}
        contactNo={props.profile.contactNo}
        clicked={handlerr}
        clicked1={changePasswordHandler}/>
    :null}


        </div>
        </>
    )
}
const mapStateToProps = state=>{
    return{
        profile:state.customer.profile
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getProfile())
        }
}

export default connect(mapStateToProps,mapDispatchToProps)(CustomerProfile)
