import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    customers:null,
    sellers:null
};

const getCustomersSuccess = (state,action)=>{
    return updateObject(state,{})

}

const saveCustomers=(state,action)=>{
    return updateObject(state,{
        customers:action.customers
    })

}

const getSellersSuccess = (state,action)=>{
    return updateObject(state,{})

}

const saveSellers=(state,action)=>{
    return updateObject(state,{
        sellers:action.sellers
    })

}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.GETCUSTOMERS_SUCCESS:
           return getCustomersSuccess(state,action)
        case actionTypes.SAVE_CUSTOMERS:
            return saveCustomers(state,action)
        case actionTypes.GETSELLER_SUCCESS:
            return getSellersSuccess(state,action)
         case actionTypes.SAVE_SELLERS:
             return saveSellers(state,action)
           default:
            return state;
        }

}

export default reducer;
