import React, { useState} from 'react'
import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from '../Login/login.module.css';
import {checkValidity} from '../../shared/utility';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
const Register = props => {

    const[authForm,setAuthForm] =  useState({
            email:{
                elementType:'input',
                elementConfig:{
                    type:'email',
                    placeholder:'your email'
                },
                value:'',
                validation:{
                    required:true,
                    isEmail: true
                },
                valid : false,
                touched:false
            },
            firstName:{
                elementType:'input',
                elementConfig:{
                    type:'firstName',
                    placeholder:'your firstName'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            lastName:{
                elementType:'input',
                elementConfig:{
                    type:'lastName',
                    placeholder:'your lastName'
                },
                value:'',
                validation:{
                    required:true,
                },
                valid : false,
                touched:false
            },
            contactNumber:{
                elementType:'input',
                elementConfig:{
                    type:'contactNumber',
                    placeholder:'your contactNumber'
                },
                value:'',
                validation:{
                    required:true
                },
                valid : false,
                touched:false
            },
            password:{
                elementType:'input',
                elementConfig:{
                    type:'password',
                    placeholder:'your password'
                },
                value:'',
                validation:{
                    required:true,
                    minLength: 6,
                    maxLength:15
                },
                valid : false,
                touched:false
            },
            confirmpassword:{
                elementType:'input',
                elementConfig:{
                    type:'password',
                    placeholder:'your confirmpassword'
                },
                value:'',
                validation:{
                    required:true,
                    minLength: 6,
                    maxLength:15
                },
                valid : false,
                touched:false
            }
        })
        if(props.registerSuccessful)
        {
            props.history.push("/emailSent")
        }

        const registerHandler=(event)=>{
            event.preventDefault();
            props.onRegister(authForm.email.value,authForm.firstName.value,authForm.lastName.value,authForm.password.value,authForm.confirmpassword.value,authForm.contactNumber.value);
        }

     const inputChangedHandler = (event,controlName)=>{
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value,authForm[controlName].validation),
                touched:true
            }
        };
        setAuthForm(updatedControls);
    }

        const formElementsArray=[];
        for(let key in authForm){
            formElementsArray.push({
                id:key,
                config:authForm[key]

            })
        }


        let form = formElementsArray.map(formElement=>(
            <React.Fragment key={formElement.id}>
            <label>{formElement.id}</label>
            <Input
            key={formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event) => inputChangedHandler(event, formElement.id)}
            invalid={!formElement.config.valid}
            shouldValidate={formElement.config.validation}
            touched={formElement.config.touched}
        />
            </React.Fragment>
        ))

        return (
            <div className={classes.Auth}>
                <form onSubmit={registerHandler}>
                 {form}
                 <button className={classes.Button}>Register</button>
                </form>
               <h5 style={{color:"red"}}>{props.error}</h5>
            </div>
        )

        }


const mapStateToProps = state=>{
    return{
        error:state.register.error,
        registerSuccessful:state.register.registerSuccessful
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onRegister: (email,firstName,lastName,password,confirmPassword,contactNumber)=>dispatch(actions.register(email,firstName,lastName,password,confirmPassword,contactNumber))
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Register)
