import React from 'react'
import classes from './singleProduct.module.css';
import {connect} from 'react-redux'

import One from '../../assets/images/1.jpg'
import Two from '../../assets/images/2.jpg'
import Three from '../../assets/images/manage-orders.jpg'

const SingleProduct=(props)=> {
    return (
      <>
      {props.singleProductForCustomer==null?null:
        <div className={classes.Container}>

  <div className={classes.LeftColumn}>
  <img src={One} alt="Los Angeles" width="70%"/>
  </div>


  <div className={classes.RightColumn}>

    <div className={classes.ProductDescription}>
      <span>{props.singleProductForCustomer.productname}</span>
      <h1>Beats EP</h1>
      <p>The preferred choice of a vast range of acclaimed DJs. Punchy, bass-focused sound and high isolation. Sturdy headband and on-ear cushions suitable for live performance</p>
    </div>



    <div className={classes.ProductPrice}>
      <span>148$</span>
      <a href="#" className={classes.CartBtn}>Add to cart</a>
    </div>
  </div>
</div>}
</>
    )
}

const mapStateToProps = state=>{
  return{
      error:state.product.error,
      singleProductForCustomer:state.product.singleProductForCustomer
  }
}

export default connect(mapStateToProps)(SingleProduct)
