import React,{useEffect} from 'react'
import CustomerProfileTable from './CustomerProfileTable'
import classes from './customerProfileTable.module.css';
import {connect} from 'react-redux';
import * as actions from '../../store/actions/index'
import One from '../../assets/images/1.jpg'
const CustomerProfile=(props)=>{
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])

    return (
        <>
        <div>
            <div className={classes.Card}>
            <div>                       
           <img className={classes.Imgs} src={One} alt="MyPic" width="200" height="200"/>
           </div>
        </div>

        <CustomerProfileTable/>
        <CustomerProfileTable/>
        </div>
        </>
    )
}
const mapStateToProps = state=>{
    console.log(state.admin.customers)
    return{
        profile:state.customer.profile
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCustomers())
        }
}

export default connect(mapStateToProps,mapDispatchToProps)(CustomerProfile)
