import React,{useEffect} from 'react';
import axios from 'axios';
import CustomerTable from './CustomerTable/CustomerTable'
import * as actions from '../../../store/actions/index'
import {connect} from 'react-redux'
const AdminGetCustomers=(props)=> {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
        console.log(props.customers)
    },[onClicked])

    return (
        <React.Fragment>
        {props.customers!==null?
        <div>
        {        
            props.customers.map((customer,index) => {
            return <CustomerTable
            key={customer.id}
            firstName={customer.firstName}
            middleName={customer.middleName}
            id={customer.id}
            lastName={customer.lastName}
            username={customer.username}
            active={customer.active}
            />
        })}
        </div>:null}

        </React.Fragment>
    )
}
const mapStateToProps = state=>{
    return{

        customers:state.admin.customers
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCustomers())
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(AdminGetCustomers)
