import React, { useEffect } from 'react';
import * as actions from '../../../store/actions/index'
import styles from './viewProductsForSeller.module.css';
import { connect } from 'react-redux'
import {withRouter} from 'react-router-dom'
const ViewProductForSeller = (props) => {

    const getProductDetailsHandler=(id)=>{
        props.getProductDetails(id)
        props.history.push("/getSingleProductForSeller")
    }
    const deleteProductHandler=(id)=>{
        props.deleteProductForSeller(id)
        window.location.reload(false)
    }
    return (
        <React.Fragment>
            {props.productsForSeller !== null ?
                props.productsForSeller.map((product, index) => {
                    return (<tr className={styles.B}>
                        <td className={styles.A}>{product.id}</td>
                        <td className={styles.A}>{product.productname}</td>
                        <td className={styles.A}>{product.brand}</td>
                        <td className={styles.A}>{product.name}</td>
                        {product.active ?
                            <td className={styles.A}>True</td> :
                            <td className={styles.A}>False</td>}
                        {product.cancellable ?
                            <td className={styles.A}>True</td> :
                            <td className={styles.A}>False</td>}
                        {product.returnable ?
                            <td className={styles.A}>True</td> :
                            <td className={styles.A}>False</td>}
                        <td className={styles.A}>
                            <button className={styles.Button1} onClick={()=>getProductDetailsHandler(product.id)}>View Details</button></td>
                        <td className={styles.A}>
                            <button className={styles.Button1} onClick={()=>deleteProductHandler(product.id)} >Delete</button></td>
                    </tr>)
                })
                : null}
        </React.Fragment>
    )
}
const mapStateToProps = state => {

    return {

        productsForSeller: state.product.productsForSeller
    }
}
const mapDispatchToProps = dispatch => {
    return {
        getProductDetails: (id)=>dispatch(actions.getSingleProductForSeller(id)),
        deleteProductForSeller:(id)=>dispatch(actions.deleteProduct(id))
    }
}
export default withRouter(connect(mapStateToProps, mapDispatchToProps)(ViewProductForSeller))
