import React,{useEffect} from 'react'
import  CustomerAddressCards from './customerAddressCards'
import {connect} from 'react-redux';
import * as actions from '../../../store/actions/index'
const CustomerAddress=(props)=> {

    const {onClicked}=props
    useEffect(()=>{
        console.log('customer profile',props.history)
        onClicked()
    },[onClicked])
    return (
        <React.Fragment>
        {props.address!==null?
        <div>
        {                   
            props.address.map((customer,index) => {
            return <CustomerAddressCards
            city={props.city}
            state={props.state}
            country={props.country}
            zipcode={props.zipcode}
            addressLine={props.addressLine}/>
        })}
        </div>:null}      
        </React.Fragment>

    )
}
const mapStateToProps = state=>{
    return{
        address:state.address.address
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getAddress())
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(CustomerAddress)
