      }
      if(props.singleProductForCustomer.fieldName[i]==="COLOR")
      {
        console.log(props.singleProductForCustomer.values[i])
        var arr = props.singleProductForCustomer.values[i].split(',')
        console.log("arr is ",arr)
        for(let j=0;j<arr.length;j++)
         {
           color.push(
             <div style={{marginLeft:"30px"}} class="checkbox">
             <input type="checkbox" id={arr[j]} value=""/>
             <label for={arr[j]}>{arr[j]}</label>
             </div>
             )
         }
        // let i = props.singleProductForCustomer.values[i]
        // var arr = i.split(',')
