import React, { useState, useEffect } from 'react'
import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from '../ChangePassword/changePassword.module.css';
import { checkValidity, updateObject } from '../../shared/utility';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
const Category = props => {

    const { onClicked } = props
    useEffect(() => {
        onClicked()
    }, [onClicked])

    const [authForm, setAuthForm] = useState({
        category: {
            elementType: 'select',
            elementConfig: {
                options: [                    
                    { value: 'fastest', displayValue: 'Fastest' },
                    { value: 'cheapest', displayValue: 'Cheapest' }
                ]
            },
            value: 'fastest',
            validation: {},
            valid: true
        },
        confirmPassword: {
            elementType: 'input',
            elementConfig: {
                type: 'confirmpassword',
                placeholder: 'your confirmpassword'
            },
            value: '',
            validation: {
                required: true,
                minLength: 6
            },
            valid: false,
            touched: false
        }
    })
    if (props.passwordChangeSuccessful) {
        if (localStorage.getItem('roleName') === "ROLE_CUSTOMER") {
            props.history.push("/account")
        }
        else {
            props.history.push("/sellerAccount")
        }
    }

    const registerHandler = (event) => {
        event.preventDefault();
        props.onSetPassword(authForm);
    }

    const inputChangedHandler = (event, controlName) => {
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value, authForm[controlName].validation),

                touched: true
            }
        };
        setAuthForm(updatedControls);
    }

    const formElementsArray = [];
    for (let key in authForm) {
        formElementsArray.push({
            id: key,
            config: authForm[key]

        })
    }


    let form = formElementsArray.map(formElement => (
        <Input
            key={formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event) => inputChangedHandler(event, formElement.id)}
            touched={formElement.config.touched}
        />
    ))

    return (
        <div className={classes.Auth}>
            <form onSubmit={registerHandler}>
                {form}
                <button className={classes.Button}>Submit</button>
            </form>
        </div>
    )

}


const mapStateToProps = state => {
    console.log(state.category.category)
    return {
        error: state.category.error,
        category: state.category.category
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onClicked: () => dispatch(actions.getCategory())
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(Category)
