import * as actionTypes from './actionTypes';
import axios from 'axios';

export const saveProductVariations = (productVariations) => {
    return {
        type: actionTypes.SAVE_ALL_PRODUCT_VARIATIONS,
        productVariations: productVariations
    }
}

export const getProductVariations = (id) => {
    return dispatch => {
        dispatch(getProductStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/getAllProductVariations/"+id, { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveProductVariations(response.data))
            })
            .catch((error) => {
            });
    }
}
