import React from 'react'
import {connect} from 'react-redux'
import {withRouter} from 'react-router-dom'
import * as actions from '../../store/actions/index'
import ProductCardsForSimilarProducts from './ProductCardsForSimilarProducts';
const SimilarProducts=(props)=> {
    const fetchSingleProductHandle=(id)=>{
      props.fetchSingleProduct(id)
      props.history.push("/singleProduct")
    }
    return (
        <>
        {props.similarProduct!=null?
        <>
        {             
            props.similarProduct.map((product) => {
            return <ProductCardsForSimilarProducts
            isActive={product.active}
            productname={product.productname}
            description={product.description}
            fetchSingleProductHandler={()=>fetchSingleProductHandle(product.id)}
            />
        })}
        </>:<p>jhfkfsjkf</p>}
        </>       
    )
       }
const mapStateToProps = state=>{
    return{
        error:state.product.error,
        similarProduct:state.product.similarProduct
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        fetchSingleProduct: (id)=>dispatch(actions.getSingleProductForCustomer(id))
        }
  }

export default withRouter(connect(mapStateToProps,mapDispatchToProps)(SimilarProducts))
