import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    address:null,
    addedSuccessfully:false,
    isUpdate:false,
    id:null
};

const getAddressStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const getAddressSuccess = (state,action)=>{
    return updateObject(state,{
        error:null,
    addedSuccessfully:true})

}

const getAddressFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}

const saveAddress=(state,action)=>{
    return updateObject(state,{
        address:action.address
    })

}

const change=(state,action)=>{
    return updateObject(state,{
        isUpdate:true,
        id:action.id,
        addedSuccessfully:true
    })
}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.GETADDRESS_START:
           return getAddressStart(state,action)
        case actionTypes.GETADDRESS_SUCCESS:
            return getAddressSuccess(state,action)
            case actionTypes.GETADDRESS_FAIL:
                return getAddressFail(state,action)
                case actionTypes.SAVE_ADDRESS:
                    return saveAddress(state,action)
                    case actionTypes.CHANGE:
                        return change(state,action)
           default:
            return state;
        }

}

export default reducer;
