import React,{useEffect} from 'react';
import * as actions from '../../store/actions/index';

import styles from '../Admin/AdminGetUser/CustomerTable/sellerTable.module.css';
import {connect} from 'react-redux'
const ViewMetadataFields=(props)=> {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])
    return (
        <React.Fragment>
        {props.metadataFields!==null?
            props.metadataFields.map((metadataFields,index) => {
            return (<tr className={styles.B}>
                <td className={styles.A}>{metadataFields.id}</td>
                <td className={styles.A}>{metadataFields.name}</td>
                <td className={styles.A}>
                <button className={styles.Button1} onClick={()=>props.onDelete(metadataFields.id)}>DELETE</button></td>
            </tr>)
        })
        :null}
        </React.Fragment>
    )
}
const mapStateToProps = state=>{
    console.log('metadata fields',state.category.metadataFields)
    return{
        metadataFields:state.category.metadataFields
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        onClicked:()=>dispatch(actions.viewCategoryMetadataField()),
        onDelete: (id) => dispatch(actions.deleteMetadata(id))
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(ViewMetadataFields)
