import React,{useEffect} from 'react';
import axios from 'axios';
import CustomerTable from './CustomerTable/CustomerTable'
import * as actions from '../../../store/actions/index'
import {connect} from 'react-redux'
const AdminGetSellers=(props)=> {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])


    const activateHandler=(id)=>{
        props.onActivate(id)
    }
    const deactivateHandler=(id)=>{
        props.onDeActivate(id)
    }


    return (
        <React.Fragment>
        {props.sellers!==null?
        <div>
        {    

            props.sellers.map((seller,index) => {
            return <CustomerTable
            key={seller.id}
            index={index+1}
            isCustomer={false}
            firstName={seller.firstName}
            middleName={seller.middleName}
            id={seller.id}
            lastName={seller.lastName}
            username={seller.username}
            companyName={seller.companyName}
            companyContact={seller.companyContact}
            city={seller.addressDTO.city}
            state={seller.addressDTO.state}
            country={seller.addressDTO.country}
            zipcode={seller.addressDTO.zipcode}
            addressLine={seller.addressDTO.addressLine}
            active={seller.active}
            activate={()=>activateHandler(seller.id)}
            deactivate={()=>deactivateHandler(seller.id)}
            />
        })}
        </div>:null}
        </React.Fragment>
    )
}
const mapStateToProps = state=>{
    console.log(state.admin.sellers)
    return{
        sellers:state.admin.sellers
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getSellers()),
        onActivate:(id)=>dispatch(actions.activateCustomer(id)),
        onDeActivate:(id)=>dispatch(actions.deactivateCustomer(id))

    }
}
export default connect(mapStateToProps,mapDispatchToProps)(AdminGetSellers)
