import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    productVariations:null,
    singleProductVariations:null
};

const saveProductVariations=(state,action)=>{
    return updateObject(state,{
        productVariations:action.productVariations
    })

}

const saveSingleProductVariation=(state,action)=>{
    return updateObject(state,{
        singleProductVariations:action.singleProductVariations
    })


}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.SAVE_ALL_PRODUCT_VARIATIONS:
           return saveProductVariations(state,action)

        case actionTypes.SAVE_SINGLE_PRODUCT_VARIATION:
            return saveSingleProductVariation(state,action)
           default:
            return state;
        }
    }


export default reducer;
