import * as actionTypes from './actionTypes';
import axios from 'axios';
export const getCustomersSuccess = () => {
    return {
        type: actionTypes.GETCUSTOMERS_SUCCESS 
    }
}

export const saveCustomers=(customers)=>{
    return{
        type:actionTypes.SAVE_CUSTOMERS,
        customers:customers
    }
}

export const getSellersSuccess = () => {
    return {
        type: actionTypes.GETSELLER_SUCCESS 
    }
}

export const saveSellers=(sellers)=>{
    return{
        type:actionTypes.SAVE_SELLERS,
        sellers:sellers
    }
}



export const getCustomers=()=>{
    return dispatch=>{
        dispatch(getCustomersSuccess())   
    const token = localStorage.getItem('token')
    const AuthStr = 'Bearer '.concat(token); 
    axios.get("/listAllCustomers", { headers: { Authorization: AuthStr } })
     .then(response => {
         dispatch(saveCustomers(response.data))
      })
     .catch((error) => {
         console.log('error ' + error);
      });      
}
}

export const getSellers=()=>{
    return dispatch=>{
        dispatch(getCustomersSuccess())   
    const token = localStorage.getItem('token')
    const AuthStr = 'Bearer '.concat(token); 
    axios.get("/listAllSellers", { headers: { Authorization: AuthStr } })
     .then(response => {
         dispatch(saveSellers(response.data))
      })
     .catch((error) => {
         console.log('error ' + error);
      });      
}
}

export const activateCustomer=(id)=>{
return dispatch=>{
    const token = localStorage.getItem('token')
    const AuthStr = 'Bearer '.concat(token); 
    axios.put("/activate/"+id, { headers: { Authorization: AuthStr } })
     .then(response => {
         dispatch(saveSellers(response.data))
      })
     .catch((error) => {
         console.log('error ' + error);
      });
}
}

export const deactivateCustomer=(id)=>{
    return dispatch=>{
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token); 
        axios.put("/deactivate/"+id, { headers: { Authorization: AuthStr } })
         .then(response => {
             dispatch(saveSellers(response.data))
          })
         .catch((error) => {
             console.log('error ' + error);
          });
    }
    }
