import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState = {
    error: null,
    category: null,
    metadataFields: null,
    fetchedSuccessful: false,
    categoriesForAdmin:null,
    id:null,
    categoriesWithoutLeaf:null,
    customerCategories:null,
    filteringDetails:null,
    flag:false,
    metadataFieldsAddedSuccessfully:false,
    addError:null,
    categoryAddedSuccessfully:false
};


const saveFilteringDetails = (state, action) => {
    return updateObject(state, {
        filteringDetails: action.filteringDetails
    })
}


const saveCategoriesForCustomer = (state, action) => {
    return updateObject(state, {
        customerCategories: action.customerCategories
    })
}
const fetchCategoryStart = (state, action) => {
    return updateObject(state, { error: null })

}
const fetchCategorySuccess = (state, action) => {
    return updateObject(state, {
        error: null,
        fetchedSuccessful: true
    })

}

const fetchCategoryFail = (state, action) => {
    return updateObject(state, {
        error: action.error,
        flag:true
    })

}

const saveCategory = (state, action) => {
    return updateObject(state, {
        category: action.category
    })

}
const saveCategoriesWithoutLeaf= (state, action) => {
    return updateObject(state, {
        categoriesWithoutLeaf: action.categoriesWithoutLeaf
    })

}
const metadataFieldsStart = (state, action) => {
    return updateObject(state, { error: null })

}
const metadataFieldsSuccess = (state, action) => {
    return updateObject(state, {
        error: null,
        fetchedSuccessful: true
    })
}

const metadataFieldsFail = (state, action) => {
    return updateObject(state, {
        error: action.error
    })

}


const metadataFieldValueSuccess = (state, action) => {
    return updateObject(state, {
        error: null,
        metadataFieldsAddedSuccessfully: true
    })
}

const metadataFieldValueFail = (state, action) => {
    return updateObject(state, {
        error: action.error
    })

}



const saveMetadataFields = (state, action) => {
    return updateObject(state, {
        metadataFields: action.metadataFields
    })

}


const saveAllCategoriesForAdmin = (state, action) => {
    return updateObject(state, {
        categoriesForAdmin: action.categoriesForAdmin
    })

}

const updateId=(state,action)=>{
    return updateObject(state,{
        id:action.id
    })
}


const addCategorySuccess=(state,action)=>{
    console.log("value changing to true")
    return updateObject(state,{
        categoryAddedSuccessfully:true
    })
}
const addCategoryFail=(state,action)=>{
    return updateObject(state,{
        addError:action.addError,
        categoryAddedSuccessfully:false
    })
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case actionTypes.FETCHCATEGORY_START:
            return fetchCategoryStart(state, action)
        case actionTypes.FETCHCATEGORY_SUCCESS:
            return fetchCategorySuccess(state, action)
        case actionTypes.FETCHCATEGORY_FAIL:
            return fetchCategoryFail(state, action)
        case actionTypes.SAVE_FETCHCATEGORY:
            return saveCategory(state, action)

        case actionTypes.FETCHMETADATAFIELD_START:
            return metadataFieldsStart(state, action)
        case actionTypes.FETCHMETADATAFIELD_SUCCESS:
            return metadataFieldsSuccess(state, action)
        case actionTypes.FETCHMETADATAFIELD_FAIL:
            return metadataFieldsFail(state, action)
        case actionTypes.SAVE_FETCHMETADATAFIELD:
            return saveMetadataFields(state, action)
            case actionTypes.SAVE_CATEGORIES_FOR_ADMIN:
                return saveAllCategoriesForAdmin(state,action)
                case actionTypes.UPDATE_ID:
                    return updateId(state,action)
                    case actionTypes.SAVE_CATEGORIES_WITHOUT_LEAF:
                        return saveCategoriesWithoutLeaf(state,action)
                        case actionTypes.SAVE_CATEGORIES_FOR_CUSTOMER:
                            return saveCategoriesForCustomer(state,action)
                            case actionTypes.SAVE_FILTERING_DETAILS_FOR_CUSTOMER:
                                return saveFilteringDetails(state,action)
                                case actionTypes.METADATA_VALUES_SUCCESS:
                                    return metadataFieldValueSuccess
                                    case actionTypes.METADATA_VALUES_FAIL:
                                        return metadataFieldValueFail
                                        case actionTypes.ADD_CATEGORY_SUCCESS:
                                            return addCategorySuccess(state,action)
                                            case actionTypes.ADD_CATEGORY_FAIL:
                                                return addCategoryFail(state,action)
        default:
            return state;
    }

}

export default reducer;
