import React from "react";
import { connect } from 'react-redux';
import * as actions from '../../store/actions/index'
const ViewCategoriesAccordian = props => {
    let content = null
        content = <div style={{ backgroundColor: '#D3D3D3', color: 'white' }} className="panel panel-default">
            <div className="panel-heading">
                <h4 className="panel-title">
                    <a data-toggle="collapse" data-parent="#accordion1" href={`#${props.id}`}>{props.name}
                    </a>
                    <div style={{ float: "right" }}>
                        <span className="glyphicon glyphicon-plus" onClick={props.clicked}></span>
                    </div>
                </h4>
            </div>

        </div>

    return (
        <div style={{ width: '200px', borderRadius: '12px', boxShadow: '30px', }} className="panel-group" id="accordion1">
            {content}
        </div>
    )
}

const mapStateToProps = state => {
    return {
        id: state.category.id
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onClicked: (name, id) => dispatch(actions.updateCategory(name, id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ViewCategoriesAccordian);
