import React, { useState, useEffect } from 'react'
import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from './login.module.css';
import { checkValidity, updateObject } from '../../shared/utility';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom'
import * as actions from '../../store/actions/index';

const Login = props => {

    const [authForm, setAuthForm] = useState({
        email: {
            elementType: 'input',
            elementConfig: {
                type: 'email',
                placeholder: 'your email'
            },
            value: '',
            validation: {
                required: true,
                isEmail: true
            },
            valid: false,
            touched: false
        },
        password: {
            elementType: 'input',
            elementConfig: {
                type: 'password',
                placeholder: 'your password'
            },
            value: '',
            validation: {
                required: true,
                minLength: 8,
                maxLength:15
            },
            valid: false,
            touched: false
        }
    })
    useEffect(() => {
        if (props.authRedirectPath !== '/') {
            props.onSetAuthRedirectPath();
        }
    }, [])

    const inputChangedHandler = (event, controlName) => {
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value, authForm[controlName].validation),
                touched: true
            }
        };
        setAuthForm(updatedControls);
    }

    const submitHandler = (event) => {
        event.preventDefault();
        props.onAuth(authForm.email.value, authForm.password.value)
    }

    const redirectToSignup = () => {
        props.history.push("/register")
    }
    const redirectToSignupAsSeller = () => {
        props.history.push("/registerAsSeller")
    }
    const formElementsArray = [];
    for (let key in authForm) {
        formElementsArray.push({
            id: key,
            config: authForm[key]

        })
    }
    let form = formElementsArray.map(formElement => (
        <Input
            key={formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event) => inputChangedHandler(event, formElement.id)}
            invalid={!formElement.config.valid}
            shouldValidate={formElement.config.validation}
            touched={formElement.config.touched}
        />
    ))
    let authRedirect = null;
    if (props.roleName==="ROLE_ADMIN"||props.roleName==="ROLE_CUSTOMER"||props.roleName==="ROLE_SELLER") {
        props.history.push("/")
    }

    return (
        <React.Fragment>
            <div className={classes.Auth}>
                <form onSubmit={submitHandler}>
                    {form}
                    <button className={classes.Button}>Login</button>
                </form>

            </div>
            <div className={classes.MyStyle}>
                <center>             <h3>
                    <label>New to this site</label>
                </h3>
                    <Link to="/forgotPassword">Forgot Password?</Link>
                    <br />
                    <br />
                    <button className={classes.Button1} onClick={redirectToSignup}>SignUp now as Customer</button>
                    <br />
                    <br />
                    <button className={classes.Button1} onClick={redirectToSignupAsSeller}>SignUp now as Seller</button>


                </center>

            </div>
        </React.Fragment>
    )

}

const mapStateToProps = state => {
    return {
        isAuthenticated: state.auth.token !== null,
        authRedirectPath: state.auth.authRedirectPath,
        roleName: state.auth.roleName
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onAuth: (email, passsword) => dispatch(actions.auth(email, passsword)),
        onSetAuthRedirectPath: () => dispatch(actions.setAuthRedirectPath('/'))
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(Login)
