import React from 'react'
import { connect } from 'react-redux'
import ProductCards from './ProductCards';
import * as actions from '../../store/actions/index'
const CategorySpecificProduct = (props) => {

    const fetchSingleProduct=(id)=>{
        props.fetchSingleProduct(id)
      }

    return (
        <>
            {props.productsForCustomer != null ?
                <>
                    {
                        props.productsForCustomer.map((product) => {
                            return <ProductCards
                                productname={product.productname}
                                description={product.description}
                                fetchSingleProductHandler={()=>fetchSingleProduct(product.id)}
                            />
                        })}
                </> : <p>dsfsdfsdf</p>}
        </>

    )
}
const mapStateToProps = state => {
    console.log(state.product.productsForCustomer, "category wale product")
    return {
        error: state.product.error,
        productsForCustomer: state.product.productsForCustomer
    }
}
const mapDispatchToProps = dispatch =>{
    return{
        fetchSingleProduct: (id)=>dispatch(actions.getSingleProductForCustomer(id))
        }
  }



export default connect(mapStateToProps,mapDispatchToProps)(CategorySpecificProduct)
