import React,{useState} from 'react'
import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from '../ChangePassword/changePassword.module.css';
import { checkValidity, updateObject } from '../../shared/utility';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
import Checkbox from '../Ui/Backdrop/Checkbox';

const AddProductVariation =(props)=> {

    let items=[]

    if(props.singleproductForSeller!==null)
    {
        for(let i=0;i<props.singleproductForSeller.fieldName.length;i++)
        {
          items.push(
              props.singleproductForSeller.fieldName[i]
              )
        }
    }


    const[authForm,setAuthForm] =  useState({
        quantity_available:{
            elementType:'input',
            elementConfig:{
                type:'quantity_available',
                placeholder:'quantity_available'
            },
            value:'',
            validation:{
                required:true,
                minLength: 6
            },
            valid : false,
            touched:false
        },
        price:{
            elementType:'input',
            elementConfig:{
                type:'price',
                placeholder:'your price'
            },
            value:'',
            validation:{
                required:true,
                minLength: 6
            },
            valid : false,
            touched:false
        }

    })

    if(props.addedSuccessfully)
    {
        props.history.push("/viewCategoriesForSeller")
    }

    const Handler=(event)=>{
        event.preventDefault();
        props.onFormSubmit(authForm,props.id);
    }

 const inputChangedHandler = (event,controlName)=>{
    const updatedControls = {
        ...authForm,
        [controlName]: {
            ...authForm[controlName],
            value: event.target.value,
            valid: checkValidity(event.target.value,authForm[controlName].validation),

            touched:true
        }
    };
    setAuthForm(updatedControls);
}

    const formElementsArray=[];
    for(let key in authForm){
        formElementsArray.push({
            id:key,
            config:authForm[key]

        })
    }


    let form = formElementsArray.map(formElement=>(
     <>
     <label>{formElement.id}</label>
        <Input
        key = {formElement.id}
        elementType={formElement.config.elementType}
        elementConfig={formElement.config.elementConfig}
        value={formElement.config.value}
        changed={(event)=>inputChangedHandler(event,formElement.id)}
        touched={formElement.config.touched}
        />
        </>
    ))

    return (
        <div className={classes.Auth}>
            <form onSubmit={Handler}>
             {form}


       { items.map(name=>{
          return  <Checkbox label={name}/>
        }
        )
    }

             <button className={classes.Button}>Submit</button>
            </form>
            <h2 style={{color:"red"}}>{props.error}</h2>

        </div>
    )

    }

const mapStateToProps = state => {
    return {
        singleproductForSeller: state.product.singleproductForSeller
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onFormSubmit: (formData,id) => dispatch(actions.addProduct(formData,id))

    }
}
export default connect(mapStateToProps,mapDispatchToProps)(AddProductVariation)
