import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    profile:null,
    updatedSuccessfully
};

const customerProfileStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const customerProfileSuccess = (state,action)=>{
    return updateObject(state,{
        error:null})

}

const customerProfileFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}

const saveProfile=(state,action)=>{
    return updateObject(state,{
        profile:action.profile
    })

}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.GETPROFILE_START:
           return customerProfileStart(state,action)
        case actionTypes.GETPROFILE_SUCCESS:
            return customerProfileSuccess(state,action)
            case actionTypes.GETPROFILE_FAIL:
                return customerProfileFail(state,action)
                case actionTypes.SAVE_PROFILE:
                    return saveProfile(state,action)
           default:
            return state;
        }

}

export default reducer;
