import React,{useEffect} from 'react'
import SellerProfileTable from './SellerProfileTable'
import classes from './sellerProfileTable.module.css';
import {connect} from 'react-redux';
import * as actions from '../../store/actions/index'
import One from '../../assets/images/1.jpg'
const SellerProfile=(props)=>{
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])

    const handlerr = ()=>{
        props.history.push("/updateProfile")
    }
    return (
        <>
        <div>
            <div className={classes.Card}>
            <div>
                <center>                       
           <img className={classes.Imgs} src={One} alt="MyPic" width="200" height="200"/>
           </center>
           </div>
        </div>
        {props.profile!==null?

        <SellerProfileTable 
        firstName={props.profile.firstName}
        middleName={props.profile.firstName}
        lastName={props.profile.lastName}
        contactNo={props.profile.companyContact}
        companyName={props.profile.companyName}
        city={props.profile.city}
        state={props.profile.state}
        country={props.profile.country}
        addressLine={props.profile.addressLine}
        zipCode={props.profile.zipCode}
        gst={props.profile.gst}
        clicked={handlerr}/>
    :null}


        </div>
        </>
    )
}
const mapStateToProps = state=>{
    return{
        profile:state.seller.profile
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getProfileOfSeller())
        }
}

export default connect(mapStateToProps,mapDispatchToProps)(SellerProfile)
