import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    forgotPasswordSuccessful:false
};

const forgotPasswordStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const forgotPasswordSuccess = (state,action)=>{
    return updateObject(state,{
        forgotPasswordSuccessful:true,
        error:null})

}

const forgotPasswordFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.FORGOTPASSWORD_START:
           return forgotPasswordStart(state,action)
        case actionTypes.FORGOTPASSWORD_SUCCESS:
            return forgotPasswordSuccess(state,action)
            case actionTypes.FORGOTPASSWORD_FAIL:
                return forgotPasswordFail(state,action)
           default:
            return state;
        }

}

export default reducer;
