import React,{useEffect} from 'react'
import axios from 'axios';
const Intro=(props)=> {

    useEffect(()=>{
        const {token} = props.match.params;
        axios.put(`/activateCustomer/${token}`).then(response=>{
        console.log(response);
        });
        })
        const pageRedirect=()=>{
            var delay = 3000;

            document.getElementById("message").innerHTML = "Please wait, you are redirecting to the login page.";

            setTimeout(function(){
             props.history.push('/login')
            },delay);

           }
        return (
            <div id="message">
                <p>Your account is activated</p>
                <input type="button" id="Okay" value="Okay" onClick={pageRedirect}></input>   
            </div>
        )
}

export default Intro
