import React from 'react'
import classes from './singleProduct.module.css';
import { connect } from 'react-redux'
import * as actions from '../../store/actions/index'
import One from '../../assets/images/1.jpg'

const SingleProduct = (props) => {
  let sizes = []
  let color = []

  if (props.singleProductForCustomer != null) {

    for (let i = 0; i < props.singleProductForCustomer.fieldName.length; i++) {
      if (props.singleProductForCustomer.fieldName[i] === "SIZE") {
        console.log(props.singleProductForCustomer.values[i])
        let arr = props.singleProductForCustomer.values[i].split(',')
        console.log("arr is ", arr)
        for (let j = 0; j < arr.length; j++) {
          sizes.push(
            <>
              <input type="checkbox" id={arr[j]} value="" />
              <label for={arr[j]}>{arr[j]}</label>
             &nbsp;&nbsp;
            </>
          )
        }
      }
      if (props.singleProductForCustomer.fieldName[i] === "COLOR") {
        console.log(props.singleProductForCustomer.values[i])
        let arr = props.singleProductForCustomer.values[i].split(',')
        console.log("arr is ", arr)
        for (let j = 0; j < arr.length; j++) {
          color.push(
            <>
              <input type="checkbox" id={arr[j]} value="" />
              <label for={arr[j]}>{arr[j]}</label>
            </>
          )
        }

      }

    }
  }


  const activateHandler = (id) => {
    props.onActivate(id)
    props.history.push("/viewCategoryForCustomer")
  }


  const deactivateHandler = (id) => {
    props.onDeactivate(id)
    props.history.push("/viewCategoryForCustomer")
  }

  const fetchSimilarProducts = (id) => {
    props.fetchSimilarProduct(id)
    props.history.push("/viewSimilarProducts")

  }

  return (
    <>
      {props.singleProductForCustomer !== null ?
        <div className={classes.Container}>

          <div className={classes.LeftColumn}>
            <img src={One} alt="Los Angeles" width="70%" />
          </div>


          <div className={classes.RightColumn}>

            <div className={classes.ProductDescription}>
              <span>{props.singleProductForCustomer.name}</span>
              <h1>{props.singleProductForCustomer.productname}</h1>
              <p>{props.singleProductForCustomer.description}</p>
            </div>
            <div>
              <h2>
                {props.singleProductForCustomer.brand}
              </h2>
              <hr />
            </div>
            <h4 style={{ color: "red" }}>SIZE AVAILABLE</h4>
            <div style={{ display: "flex" }}>
              {sizes}
            </div>
            <h4 style={{ color: "red" }}>COLOR AVAILABLE</h4>
            <div style={{ display: "flex" }}>
              {color}
            </div>

            <hr />

            <div className={classes.ProductPrice}>
              {localStorage.getItem('roleName') === "ROLE_CUSTOMER" ?
                <>
                         <button className={classes.CartBtn} onClick={() => fetchSimilarProducts(props.singleProductForCustomer.id)}>Similar Products</button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;

                  <button className={classes.CartBtn}>Add to cart</button>&nbsp;&nbsp;&nbsp;&nbsp;
              <button className={classes.CartBtn}>Buy Now</button></> : null}
              {localStorage.getItem('roleName') === "ROLE_ADMIN" ?
                props.singleProductForCustomer.active ?
                  <button className={classes.CartBtn} onClick={() => deactivateHandler(props.singleProductForCustomer.id)} >Deactivate</button>
                  :
                  <button className={classes.CartBtn} onClick={() => activateHandler(props.singleProductForCustomer.id)}>Activate</button> : null}

            </div>
          </div>
        </div> : null}
    </>
  )
}


const mapStateToProps = state => {
  return {
    error: state.product.error,
    singleProductForCustomer: state.product.singleProductForCustomer
  }
}

const mapDispatchToProps = dispatch => {
  return {
    onDeactivate: (id) => dispatch(actions.deactivateProduct(id)),
    fetchSimilarProduct:(id)=>dispatch(actions.getSimilarProduct(id)),
    onActivate: (id) => dispatch(actions.activateProduct(id))
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(SingleProduct)
