import React, { Component } from "react";
import { connect } from 'react-redux';
import * as actions from '../../store/actions/index'
const ViewCategoriesAccordian = props => {
    const handler = (id) => {
        if (document.getElementById(id).value !== '') {
            props.onClicked(document.getElementById(id).value, props.id)
            console.log("name is", document.getElementById(id).value)
            window.location.reload(false)
        }
        else {
            window.location.reload(false)
        }
    }
    let content = null
    if (localStorage.getItem('roleName') === "ROLE_SELLER") {


        content = <div style={{ backgroundColor: '#D3D3D3', color: 'white' }} class="panel panel-default">
            <div class="panel-heading">
                <h4 class="panel-title">

                    <a data-toggle="collapse" data-parent="#accordion1" href={`#${props.id}`}>{props.name}
                    </a>
                    <div style={{ float: "right" }}>
                        <span class="glyphicon glyphicon-plus"></span>
                    </div>
                </h4>
            </div>
            <div id={props.id} class="panel-collapse collapse">

                <div class="panel-body">

                    <div class="panel-group" id="accordion2">
                        <div style={{ backgroundColor: '#D3D3D3' }} class="panel panel-default">
                            <div class="panel-heading">
                                <h4 class="panel-title">
                                    <a data-toggle="collapse" data-parent="#accordion2" href={`#${props.id}1`}>FIELDS
                         </a>
                                </h4>
                            </div>
                            <div id={`${props.id}1`} class="panel-collapse collapse in">
                                <div class="panel-body">{props.a}</div>
                            </div>
                        </div>
                        <div style={{ backgroundColor: '#D3D3D3' }} class="panel panel-default">
                            <div class="panel-heading">
                                <h4 class="panel-title">
                                    <a data-toggle="collapse" data-parent="#accordion2" href={`#${props.id}2`}>VALUES
                         </a>
                                </h4>
                            </div>
                            <div id={`${props.id}2`} class="panel-collapse collapse">
                                <div class="panel-body">{props.b}</div>
                            </div>
                        </div>
                    </div>

                </div>
            </div>
        </div>


    }
    else {



        content = <div style={{ backgroundColor: '#D3D3D3', color: 'white' }} class="panel panel-default">
            <div class="panel-heading">
                <h4 class="panel-title">
                    <a data-toggle="collapse" data-parent="#accordion1" href={`#${props.id}`}>{props.name}
                    </a>
                    <div style={{ float: "right" }}>
                        <span class="glyphicon glyphicon-plus" onClick={props.updateId} onClick={props.clicked}></span>
                    </div>
                </h4>
            </div>

        </div>

    }
    return (
        <div style={{ width: '200px', borderRadius: '12px', boxShadow: '30px', }} class="panel-group" id="accordion1">
            {content}

        </div>
    )
}

const mapStateToProps = state => {
    console.log("id is" + state.category.id)
    return {
        id: state.category.id
    }
}
const mapDispatchToProps = dispatch => {
    return {
        onClicked: (name, id) => dispatch(actions.updateCategory(name, id))
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(ViewCategoriesAccordian);
