import React,{useEffect} from 'react'
import {connect} from 'react-redux';
import * as actions from '../../../store/actions/index'
import ViewCategoriesAccordian from '../ViewCategoriesAccordian'
import classes from '../../ChangePassword/changePassword.module.css';
import SelleAccordian from '../SelleAccordian';
const ViewAllCategoriesForAdmin=(props)=> {

    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])

    const updateIdOfCategory=(id)=>{
        props.updateId(id)
        props.history.push("/addProduct")
    }

    return (
        <div className={classes.Auth}>
        {props.category!==null?
        <>
        {                   
            props.category.map((categoriesForAdmin) => {
               let arr = Object.values(categoriesForAdmin)
               let a=''
               if(arr[2]!==null)
               {
               for(let i=0;i<arr[2].length;i++)
               {
                  a=a.concat(arr[2][i]+" ")
               }
               console.log('a is' ,a)
            }
            let b=' '
            if(arr[3]!==null)
            {
            for(let i=0;i<arr[3].length;i++)
            {
               b=b.concat(arr[3][i]+" ")
            }
            console.log('b is' ,b)
         }


            return <SelleAccordian
            name={categoriesForAdmin.name}
            key={categoriesForAdmin.id}
            a={a}
            b={b}
            cid={categoriesForAdmin.id}
            update={()=>updateIdOfCategory(categoriesForAdmin.id)}

            />
        })}
        </>:null}

        </div>

    )
}
const mapStateToProps = state=>{
    return{
        category:state.category.category
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCategory()),
        updateId:(id)=>dispatch(actions.updateId(id))
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(ViewAllCategoriesForAdmin)
