import * as actionTypes from './actionTypes';
import axios from 'axios';
export const forgotPasswordSuccess = () => {
    return {
        type: actionTypes.FORGOTPASSWORD_SUCCESS,
    }
}

export const forgotPasswordFail = (error) => {
    console.log('auth fail')
    return {
        type: actionTypes.FORGOTPASSWORD_FAIL,
        error: error
    }
}

export const forgotPasswordStart=()=>{
    return{
        type: actionTypes.FORGOTPASSWORD_START
    }
}

export const setPasswordSuccess=()=>{
    return{
        type: actionTypes.SETPASSWORD_SUCCESS
    }
}

export const forgotPassword=(email)=>{
    return dispatch=>{
        dispatch(forgotPasswordStart())
        axios.post("/forgotPassword/"+email).then(response=>{
            dispatch(forgotPasswordSuccess())
        }).catch(error=>{
            dispatch(forgotPasswordFail(error.response.data.error))
        })
    }
}

export const setPassword=(password,confirmPassword,token)=>{
    return dispatch=>{
        dispatch(setPasswordSuccess())
        axios.post("/setPassword/"+token,{
            password:password,
            confirmPassword:confirmPassword
        }).then(response=>{
            console.log(response)
        })

    }

}
