import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    passwordChangeSuccessful:false,
};

const changePasswordStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const changePasswordSuccess = (state,action)=>{
    return updateObject(state,{
        passwordChangeSuccessful:true,
        error:null})

}

const changePasswordFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}


const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.CHNAGEPASSWORD_START:
           return changePasswordStart(state,action)
        case actionTypes.CHANGEPASSWORD_SUCCESSS:
            return changePasswordSuccess(state,action)
            case actionTypes.CHANGEPASSWORD_FAIL:
                return changePasswordFail(state,action)
           default:
            return state;
        }

}

export default reducer;
