import React from 'react'
import {connect} from 'react-redux'
import { Col } from 'react-bootstrap';
import {Panel} from 'react-bootstrap'
import  classes from './product.module.css'

import One from '../../assets/images/1.jpg'
import Two from '../../assets/images/2.jpg'
import Three from '../../assets/images/manage-orders.jpg'
import ProductCards from './ProductCards';
const Product=(props)=> {
    return (

        <>
        <div id="carouselExampleSlidesOnly" class="carousel slide" data-ride="carousel">
  <div class="carousel-inner">
    <div class="carousel-item active">
      <img class="d-block w-100" src={One} alt="First slide"/>
    </div>
    <div class="carousel-item">
      <img class="d-block w-100" src={Two} alt="Second slide"/>
    </div>
    <div class="carousel-item">
      <img class="d-block w-100" src={Three} alt="Third slide"/>
    </div>
  </div>
</div>

        {props.products!=null?
        <>
        {                   
            props.products.map((product) => {
            return <ProductCards
            productname={product.productname}
            description={product.description}
            />
        })}
        </>:null}
        </>

    )
       }
const mapStateToProps = state=>{
    console.log(state.product.products,"products    in Product")
    return{
        error:state.product.error,
        products:state.product.products
    }
}


export default connect(mapStateToProps)(Product)
