import React from 'react';
import Logo from '../../Logo/Logo';
import Navigationitems from '../NavigationItems/NavigationItems';
import classes from './sideDrawer.module.css';
import Backdrop from '../../Ui/Backdrop/Backdrop';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom'
import styles from '../NavigationItems/navigationItems.module.css'
import styles1 from '../NavigationItem/navigationItem.module.css'

const SideDrawer = (props) => {

    let attachedClasses = [classes.SideDrawer, classes.Close]
    if (props.open) {
        attachedClasses = [classes.SideDrawer, classes.Open]
    }
    let content = null
 let role = localStorage.getItem('roleName')
 if(role==="ROLE_ADMIN")
 {
     content=props.isAuth?(<nav>
         <ul className={styles.NavigationItems}>
             <li className={styles1.NavigationItem}><Link to="/getSellers">Get Sellers</Link></li>
             <li className={styles1.NavigationItem}><Link to="/getCustomers">Get Customers</Link></li>
         </ul>
     </nav>):null
 }
    return (
        <React.Fragment>
            <Backdrop show={props.open} clicked={props.closed} />
              <div className={attachedClasses.join(' ')} onClick={props.closed}>
                <div className={classes.Logo}>
                <Logo/>
                </div>
                {content}
                <nav>
      <Navigationitems isAuth={props.isAuth}/>
      </nav>

            </div>      
            </React.Fragment>
    );
};

export default SideDrawer;
