import React from 'react';
import classes from './toolbar.module.css';
import Logo from '../../Logo/Logo';
import {Link} from 'react-router-dom';
import NavigationItems from '../NavigationItems/NavigationItems';
import DrawerToggle from '../SideDrawer/DrawerToggle/DrawerToggle';
const toolbar = (props)=>(
        <header className={classes.Toolbar}>
        <DrawerToggle clickedd={props.drawerToggleClicked}/>
        <Logo height="80%"/>
        <div className={classes.SearchBar}>
        <input type="text" placeholder="Search...."/>
        <button>Search</button>
        </div>
        <nav className={classes.DesktopOnly}>
        <NavigationItems isAuth={props.isAuth}/>
        </nav>
        {props.isAuth?
        <div class={classes.Dropdown}>
        <button class={classes.Dropbtn}>{props.firstName}&nbsp;&nbsp;<span className="glyphicon glyphicon-user"></span></button>
        <div class={classes.DropdownContent}>
        <Link to="/getCustomers">Get Customers</Link>
        <Link to="/getSellers">Get Sellers</Link>
        </div>
        </div>:null}
        </header>
);
export default toolbar;
