import * as actionTypes from './actionTypes';
import axios from 'axios';
export const registerStart = () => {
    return {
        type: actionTypes.REGISTER_START
    }
}

export const registerSuccess = () => {
    return {
        type: actionTypes.REGISTER_SUCCESS 
    }
}

export const registerFail = (error) => {
    return {
        type: actionTypes.AUTH_FAIL,
        error: error
    }
}

export const register=(email,firstName,lastName,password,confirmPassword,contactNumber)=>{
    return dispatch=>{
        dispatch(registerStart());
        const data={
            'username':email,
            'firstName':firstName,
            'lastName':lastName,
            'password':password,
            'confirmPassword':confirmPassword,
            'contactNo':contactNumber
        }
        axios.post("/registerAsCustomer",data).then((response=>{
                dispatch(registerSuccess());
        })).catch((error)=>{
            dispatch(registerFail(error.response.data.message))
        })
    }
}
export const registerAsSeller=(formData)=>{
    return dispatch=>{
        dispatch(registerStart());
        const data= {
            "username": formData.email.value,
            "password": formData.password.value,
            "confirmPassword": formData.confirmpassword.value,
            "firstName": formData.firstName.value,
            "lastName": formData.lastName.value,
            "companyName":formData.companyName.value,
            "companyContactNo":formData.companyContactNo.value,
            "city":formData.city.value,
            "state":formData.state.value,
            "country":formData.country.value,
            "zipcode":formData.zipcode.value,
            "addressLine":formData.addressLine.value,
            "gstNo":formData.gstNo.value
     }
     axios.post("/registerAsSeller",data).then((response=>{
        dispatch(registerSuccess())
        console.log("register successful")
    })).catch((error)=>{
        console.log(error)
    })

    }
}
