import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    profile:null
};

const sellerProfileStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const sellerProfileSuccess = (state,action)=>{
    return updateObject(state,{
        error:null})

}

const sellerProfileFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}

const saveSellerProfile=(state,action)=>{
    return updateObject(state,{
        profile:action.profile
    })

}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.GETSELLERPROFILE_START:
           return sellerProfileStart(state,action)
        case actionTypes.GETSELLERPROFILE_SUCCESS:
            return sellerProfileSuccess(state,action)
            case actionTypes.GETSELLERPROFILE_FAIL:
                return sellerProfileFail(state,action)
                case actionTypes.SAVE_SELLERPROFILE:
                    return saveSellerProfile(state,action)
           default:
            return state;
        }

}

export default reducer;
