import React, { useState,useEffect } from 'react'
import Input from '../../Ui/Backdrop/Input/Input';
import classes from '../../Login/login.module.css';
import {checkValidity,updateObject} from '../../../shared/utility';
import { connect } from 'react-redux';
import * as actions from '../../../store/actions/index';
const UpdateAddress = props => {
    const {onClicked}=props
    useEffect(()=>{
        onClicked()
    },[onClicked])    

    const[authForm,setAuthForm] =  useState({
            city:{
                elementType:'input',
                elementConfig:{
                    type:'city',
                    placeholder:'your city'
                },
                value:props.address.city,
                validation:{
                    required:true,
                    isEmail: true
                },
                valid : false,
                touched:false
            },
            state:{
                elementType:'input',
                elementConfig:{
                    type:'state',
                    placeholder:'your state'
                },
                value:props.address.state,
                validation:{
                    required:true,
                    isEmail: true
                },
                valid : false,
                touched:false
            },
            country:{
                elementType:'input',
                elementConfig:{
                    type:'country',
                    placeholder:'your country'
                },
                value:props.address.country,
                validation:{
                    required:true,
                    minLength: 6
                },
                valid : false,
                touched:false
            },
            zipcode:{
                elementType:'input',
                elementConfig:{
                    type:'zipcode',
                    placeholder:'your zipcode'
                },
                value:props.address.zipcode,
                validation:{
                    required:true,
                    minLength: 6
                },
                valid : false,
                touched:false
            },
            addressLine:{
                elementType:'input',
                elementConfig:{
                    type:'addressLine',
                    placeholder:'your addressLine'
                },
                value:props.address.addressLine,
                validation:{
                    required:true,
                    minLength: 6
                },
                valid : false,
                touched:false
            }
        })




        const submitHandler=(event)=>{
            event.preventDefault();
            props.onAdd(authForm)
            if (props.addedSuccessfully) {
                props.history.push("/addresses")
            }

        }

     const inputChangedHandler = (event,controlName)=>{
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value,authForm[controlName].validation),

                touched:true
            }
        };
        setAuthForm(updatedControls);
    }

        const formElementsArray=[];
        for(let key in authForm){
            formElementsArray.push({
                id:key,
                config:authForm[key]

            })
        }

        let form = formElementsArray.map(formElement=>(

            <Input
            key = {formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event)=>inputChangedHandler(event,formElement.id)}
            touched={formElement.config.touched}
            />
        ))

        return (
            <React.Fragment>
            <div className={classes.Auth}>

                <form onSubmit={submitHandler}>
                 {form}
                 <button className={classes.Button}>Submit</button>
                </form>

            </div>
             </React.Fragment>
        )

        }
        const mapStateToProps = state=>{
            console.log('id is'+state.address.id)
            return{
                address:state.address.address,
                addedSuccessfully:state.address.addedSuccessfully,
                id:state.address.id
            }
        }

        const mapDispatchToProps = dispatch =>{
            return{
                onClicked: ()=>dispatch(actions.getAddress()),
                onAdd:(formData)=>dispatch(actions.addNewAddress(formData))
                }
        }
export default connect(mapStateToProps,mapDispatchToProps)(UpdateAddress)
