import React from 'react'
import {connect} from 'react-redux'
import ProductCards from './ProductCards';
const CategorySpecificProduct=(props)=> {
    return (
        <>
        {props.productsForCustomer!=null?
        <>
        {                   
            props.productsForCustomer.map((product) => {
            return <ProductCards
            productname={product.productname}
            description={product.description}
            />
        })}
        </>:null}
        </>

    )
       }
const mapStateToProps = state=>{
    console.log(state.product.productsForCustomer,"category wale product")
    return{
        error:state.product.error,
        productsForCustomer:state.product.productsForCustomer
    }
}


export default connect(mapStateToProps)(CategorySpecificProduct)
