import React, { useState, useEffect } from 'react'
import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from '../ChangePassword/changePassword.module.css';
import { checkValidity, updateObject } from '../../shared/utility';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
const UpdateMetadataValues = props => {
    console.log(document.referrer,"props")

    let option=[]
    let option1=[]   
    if(props.category!==null)
    {
    for(let i=0;i<props.categoriesForAdmin.length;i++)
    {
        if(props.id===props.categoriesForAdmin[i].id)
        {
        option.push({
            value:props.categoriesForAdmin[i].id,
            displayValue:props.categoriesForAdmin[i].name
        })


    }
    }
}
if (Array.isArray(option1) && option1.length) 
{
    props.history.push("/adminDashboard")
}


//     if(props.metadataFields!==null)
//     {
//     for(let i=0;i<props.metadataFields.length;i++)
//     {
//         console.log(props.metadataFields)
//         option1.push({
//             value:props.metadataFields[i].id,
//             displayValue:props.metadataFields[i].name
//         })
//     }
// }

    const [authForm, setAuthForm] = useState({
        category: {
            elementType: 'select',
            elementConfig: {
                options: option
            },
            value: option[0].value,
            validation: {},
            valid: true
        },
        metadataFields: {
            elementType: 'select',
            elementConfig: {
                options: option1
            },
            value: option1[0].value,
            validation: {},
            valid: true
        },
        categoryMetadataField: {
            elementType: 'input',
            elementConfig: {
                type: 'categoryMetadataField',
                placeholder: 'your categoryMetadataFieldValues'
            },
            value: '',
            validation: {
                required: true,
                minLength: 6
            },
            valid: false,
            touched: false
        }
    })

    const registerHandler = (event) => {
        event.preventDefault();
        props.onAdd(authForm.category.value,authForm.metadataFields.value,authForm.categoryMetadataField.value);
    }

    const inputChangedHandler = (event, controlName) => {
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value, authForm[controlName].validation),

                touched: true
            }
        };
        setAuthForm(updatedControls);
    }

    const formElementsArray = [];
    for (let key in authForm) {
        formElementsArray.push({
            id: key,
            config: authForm[key]

        })
    }

    let form = formElementsArray.map(formElement => (
        <Input
            key={formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event) => inputChangedHandler(event, formElement.id)}
            touched={formElement.config.touched}
        />
    ))

    return (
        <div className={classes.Auth}>
            <form onSubmit={registerHandler}>
                {form}
                <button className={classes.Button}>Submit</button>
            </form>
        </div>
    )

}


const mapStateToProps = state => {

    return {
        error: state.category.error,
        categoriesForAdmin:state.category.categoriesForAdmin,
        metadataFields:state.category.metadataFields,
        id:state.category.id
    }
}

const mapDispatchToProps = dispatch => {
    return {
        onClicked: ()=>dispatch(actions.getAllCategoriesForAdmin()),

    }
}
export default connect(mapStateToProps, mapDispatchToProps)(UpdateMetadataValues)
