import React, { useEffect } from 'react';
import { Route, Switch, withRouter, Redirect } from 'react-router-dom';
import Login from './components/Login/Login'
import Welcome from './components/Welcome'
import Layout from './components/Layout/Layout';
import Intro from './Intro';
import Register from './components/Register/Register'
import { connect } from 'react-redux';
import * as actions from './store/actions/index'
import './App.css';
import RegisterAsSeller from './components/RegisterAsSeller/RegisterAsSeller';
import Logout from './components/Logout'
import ForgotPassword from './components/ForgotPassword/ForgotPassword';
import EmailSent from './components/EmailSent/EmailSent';
import SetPassword from './components/SetPassword/SetPassword';
import Account from './components/Account/Account'
import AdminDashboard from './components/Admin/AdminDashboard/AdminDashboard';
import AdminGetCustomers from './components/Admin/AdminGetUser/AdminGetCustomers';
import AdminGetSellers from './components/Admin/AdminGetUser/AdminGetSellers';
import CustomerProfile from './components/Customer/CustomerProfile';
import CustomerTable from './components/Admin/AdminGetUser/CustomerTable/CustomerTable';
import SellerTable from './components/Admin/AdminGetUser/CustomerTable/SellerTable'
import UpdateProfile from './components/Customer/UpdateProfile'
import CustomerAddress from './components/Customer/CustomerAddress/CustomerAddress';
import AddNewAddress from './components/Customer/CustomerAddress/AddNewAddress';
import UpdateAddress from './components/Customer/CustomerAddress/UpdateAddress';
import ChangePassword from './components/ChangePassword/ChangePassword';
import SellerProfile from './components/Seller/SellerProfile';
import SellerAccount from './components/Seller/SellerAccount';
import UpdateProfileForSeller from './components/Seller/UpdateProfileForSeller';
import SellerAddress from './components/Seller/SellerAddress';
import ViewProductTable from './components/Product/ViewProductForSeller/ViewProductTable'
import Try from './Try/Try';
import Category from './components/Category/Category';
import AddMetadataField from './components/Category/AddMetadataField';
import MetadataTable from './components/Category/MetadataTable';
import ViewAllCategoriesForAdmin from './components/Category/ViewAllCategoriesForAdmin';
import ViewCategoriesForSeller from './components/Category/ViewCategoriesForSeller/ViewCategoriesForSeller'
import UpdateMetadataValues from './components/Category/UpdateMetadataValues';
import AddCategory from './components/Category/AddCategory';
import AddSubCategory from './components/Category/AddSubCategory';
import ViewAllCategoriesForCustomer from './components/Category/VIewAllCategoriesForCustomer/ViewAllCategoriesForCustomer';
import Navbar from './components/NavBar/Navbar';
import Footer from './components/Footer/Footer';
import SingleProduct from './components/Product/SingleProduct';
import AddProduct from './components/Product/AddProduct/AddProduct';
import ViewSingleProductForSeller from './components/Product/ViewProductForSeller/ViewSingleProductForSeller/ViewSingleProductForSeller';
import UpdateProduct from './components/Product/UpdateProduct/UpdateProduct';
import AddProductVariation from './components/ProductVariation/AddProductVariation';
import ErrorOccured from './components/ErrorOccured/ErrorOccured';
const App = props => {
  const { onTryAutoSignup } = props;
  useEffect(() => {
    onTryAutoSignup();
  }, [onTryAutoSignup])
  //anonymous
  let routes = (
    <Switch>
      <Route path="/" exact component={Welcome} />
      <Route path="/login" exact component={Login} />
      <Route path="/register" exact component={Register} />
      <Route path="/registerAsSeller" exact component={RegisterAsSeller} />
      <Route path="/verify/:token" component={Intro} />
      <Route path="/forgotPassword" exact component={ForgotPassword} />
      <Route path="/emailSent" exact component={EmailSent} />
      <Route path="/setPassword/:token" component={SetPassword} />
      <Route path="/logout" exact component={Logout} />

      <Redirect to="/"/>
    </Switch>
  );

  if (props.isAuthenticated && props.roleName === "ROLE_CUSTOMER") {
    routes = (
      <Switch>
        <Route path="/" exact component={Welcome} />
        <Route path="/logout" exact component={Logout} />

      <Route path="/login" exact component={Login} />
        <Route path="/account" component={Account} />
        <Route path="/profile" component={CustomerProfile} />
        <Route path="/updateProfile" component={UpdateProfile} />
        <Route path="/addresses" component={CustomerAddress} />
        <Route path="/addNewAddress" component={AddNewAddress} />
        <Route path="/updateAddress" component={UpdateAddress} />
        <Route path="/changePassword" component={ChangePassword} />
        <Route path="/viewCategoryForCustomer" component={Navbar}/>
        <Route path="/singleProduct" component={SingleProduct}/>
        <Route component={ErrorOccured}/>
        <Redirect to="/"/>
      </Switch>
    )
  }

  if (props.isAuthenticated && props.roleName === "ROLE_SELLER") {
    routes = (
      <Switch>
        <Route path="/" exact component={Welcome} />
        <Route path="/logout" exact component={Logout} />
        <Route path="/sellerProfile" component={SellerProfile} />

      <Route path="/login" exact component={Login} />
        <Route path="/sellerAccount" component={SellerAccount} />
        <Route path="/updateProfileForSeller" component={UpdateProfileForSeller} />
        <Route path="/updateSellerAddress" component={SellerAddress} />
        <Route path="/addProduct" component={AddProduct} />
        <Route path="/viewAllProductsForSeller" component={ViewProductTable} />
        <Route path="/getSingleProductForSeller" component={ViewSingleProductForSeller} />
        <Route path="/updateProductForSeller" component={UpdateProduct} />
        <Route path="/addAProductVariation" component={AddProductVariation} />
        <Route path="/viewCategoriesForSeller" component={ViewCategoriesForSeller} />
      </Switch>
    )
  }

  if (props.isAuthenticated && props.roleName === "ROLE_ADMIN") {
    routes = (
      <Switch>
        <Route path="/" exact component={Welcome} />
        <Route path="/logout" exact component={Logout} />
        <Route path="/adminDashboard" exact component={AdminDashboard} />

      <Route path="/login" exact component={Login} />
        <Route path="/getCustomers" component={CustomerTable} />
        <Route path="/getSellers" component={SellerTable} />
        <Route path="/changePassword" component={ChangePassword} />
        <Route path="/singleProduct" component={SingleProduct} />
        <Route path="/addMetadataFields" component={Category} />
        <Route path="/addMetadatFieldName" component={AddMetadataField} />
        <Route path="/viewMetadataField" component={MetadataTable} />
        <Route path="/viewAllCategoriesForAdmin" component={ViewAllCategoriesForAdmin} />
        <Route path="/updateMetadataValues" component={UpdateMetadataValues} />
        <Route path="/addACategory" component={AddCategory} />
        <Route path="/addASubCategory" component={AddSubCategory} />
        <Route path="/viewCategoryForCustomer" component={Navbar} />
      </Switch>
    );
  }
  return (
    <div>
      <Layout>
        {routes}
      </Layout>
    </div>
  );
}
const mapStateToProps = state => {
  console.log(state.auth.token,"token in app js")
  return {
    isAuthenticated: state.auth.token !== null,
    roleName: state.auth.roleName
  };
};
const mapDispatchToProps = dispatch => {
  return {
    onTryAutoSignup: () => dispatch(actions.authCheckState())
  };
};
export default withRouter(connect(mapStateToProps, mapDispatchToProps)(App));
