import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    products:null,
    updatedSuccessfully:false,
    productsForCustomer:null,
    singleProductForCustomer:null,
    addedSuccessfully:false,
    productsForSeller:null,
    singleproductForSeller:null
};

const getProductStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const getProductSuccess = (state,action)=>{
    return updateObject(state,{
        error:null,
    updatedSuccessfully:true})

}

const getProductFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}

const saveProducts=(state,action)=>{
    return updateObject(state,{
        products:action.products
    })

}

const saveProductsForSeller=(state,action)=>{
    return updateObject(state,{
        productsForSeller:action.productsForSeller
    })

}

const saveSingleProductsForSeller=(state,action)=>{
    return updateObject(state,{
        singleproductForSeller:action.singleproductForSeller
    })

}


const saveProductsForCustomer=(state,action)=>{
    return updateObject(state,{
        productsForCustomer:action.productsForCustomer
    })

}
const saveSingleProductForCustomer=(state,action)=>{
    return updateObject(state,{
        singleProductForCustomer:action.singleProductForCustomer
    })

}

const productAddedSuccessfully=(state,action)=>{
    return updateObject(state,{
        addedSuccessfully:true
    })
}
const addProductFail=(state,action)=>{
    return updateObject(state,{
        addedSuccessfully:false,
        error:action.error
    })
}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.GETPRODUCT_START:
           return getProductStart(state,action)
        case actionTypes.GETPRODUCT_SUCCESS:
            return getProductSuccess(state,action)
            case actionTypes.GETPRODUCT_FAIL:
                return getProductFail(state,action)
                case actionTypes.SAVE_GETPRODUCT:
                    return saveProducts(state,action)
                    case actionTypes.SAVE_PRODUCTS_FOR_CUSTOMER:
                        return saveProductsForCustomer(state,action)
                        case actionTypes.SAVE_SINGLE_PRODUCT_FOR_CUSTOMER:
                            return saveSingleProductForCustomer(state,action)
                            case actionTypes.PRODUCT_ADDED_SUCCESSFULLY:
                                return productAddedSuccessfully(state,action)
                                case actionTypes.ADD_PRODUCT_FAIL:
                                    return addProductFail(state,action)
                                    case actionTypes.SAVE_PRODUCTS_FOR_SELLER:
                                        return saveProductsForSeller(state,action)
                                        case actionTypes.SAVE_SINGLE_PRODUCT_FOR_SELLER:
                                            return saveSingleProductsForSeller(state,action)
           default:
            return state;
        }

}

export default reducer;
