import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../../shared/utility';

const initialState={
    error:null,
    category:null,
    fetchedSuccessful:false
};

const fetchCategoryStart = (state,action)=>{
    return updateObject(state,{error:null})

}
const fetchCategorySuccess = (state,action)=>{
    return updateObject(state,{
        error:null,
    fetchedSuccessful:true})

}

const fetchCategoryFail=(state,action)=>{
    return updateObject(state,{
        error:action.error
    })

}

const saveCategory=(state,action)=>{
    return updateObject(state,{
        category:action.category
    })

}

const reducer =(state=initialState,action)=>{
    switch (action.type){
        case actionTypes.FETCHCATEGORY_START:
           return fetchCategoryStart(state,action)
        case actionTypes.FETCHCATEGORY_SUCCESS:
            return fetchCategorySuccess(state,action)
            case actionTypes.FETCHCATEGORY_FAIL:
                return fetchCategoryFail(state,action)
                case actionTypes.SAVE_FETCHCATEGORY:
                    return saveCategory(state,action)
           default:
            return state;
        }

}

export default reducer;
