import React from 'react'
import {connect} from 'react-redux';
import classes from './ViewAllProductVariation/viewAllProductVariationsCards.module.css'

const SingleProductVariation=(props)=> {
    let option=[]
    if(props.singleProductVariations!=null)
    {
    for(let i=0;i<props.singleProductVariations.fields.length;i++)
    {
          option.push(
          <>
          <p>{props.singleProductVariations.fields[i]}:{props.singleProductVariations.values[i]}</p>
            </>)
    }
}
    return (
        <>
        {props.singleProductVariations!==null?
           <>
            <div  className={classes.Card1} >
            <div>
                <img className={classes.Imgs} src="https://dummyimage.com/600x400/55595c/fff" alt="MyPic"  />
            </div>
            <div className={classes.Container} style={{float:"left"}} onClick={props.fetchSingleVariation}>
               <h4><b>ProductName: {props.singleProductVariations.productname}</b></h4> 
               <h4><b>Price: {props.singleProductVariations.price}</b></h4>
               <h4><b>Quantity: {props.singleProductVariations.quantityAvailable}</b></h4>
               <h4><b>{option}</b></h4>

            </div>
        </div>
        </>:null}
        </>

    )
}

const mapStateToProps = state=>{
    console.log(state.productVariation.singleProductVariations)
    return{
        singleProductVariations:state.productVariation.singleProductVariations
    }
}

export default connect(mapStateToProps,null)(SingleProductVariation)
