import * as actionTypes from './actionTypes';
import axios from 'axios';
export const sellerProfileStart = () => {
    return {
        type: actionTypes.GETSELLERPROFILE_START
    }
}
export const sellerProfileSuccess = () => {
    return {
        type: actionTypes.GETSELLERPROFILE_SUCCESS
    }
}

export const sellerProfileFail = (error) => {
    return {
        type: actionTypes.GETSELLERPROFILE_FAIL,
        error: error
    }
}

export const saveSellerProfile = (profile) => {
    return {
        type: actionTypes.SAVE_SELLERPROFILE,
        profile: profile
    }
}

export const getProfileOfSeller = () => {
    return dispatch => {
        dispatch(sellerProfileStart())
        const token = localStorage.getItem('token')
        const AuthStr = 'Bearer '.concat(token);
        axios.get("/viewProfileForSeller", { headers: { Authorization: AuthStr } })
            .then(response => {
                dispatch(saveSellerProfile(response.data))
                dispatch(sellerProfileSuccess())
            })
            .catch((error) => {
                dispatch(sellerProfileFail())
                console.log('error ' + error);
            });
    }
}

