import React,{useEffect} from 'react'
import {connect} from 'react-redux';
import * as actions from '../../store/actions/index'
import ViewCategoriesAccordian from './ViewCategoriesAccordian'
import classes from '../ChangePassword/changePassword.module.css';
import AdminAccordian from './AdminAccordian';
const ViewAllCategoriesForAdmin=(props)=> {

    const {onClicked,onClicked1}=props
    useEffect(()=>{
        onClicked()
        onClicked1()
    },[onClicked,onClicked1])

    const updateCategory=(id)=>{
        props.updateId(id)
    }

    const handler=(id)=>{
        props.updateId(id)
        props.history.push("/updateMetadataValues")

    }

    return (
        <div className={classes.Auth}>
        {props.categoriesForAdmin!==null?
        <>
        {                   
            props.categoriesForAdmin.map((categoriesForAdmin) => {
               let arr = Object.values(categoriesForAdmin)
               let a=''
               if(arr[2]!==null)
               {
               for(let i=0;i<arr[2].length;i++)
               {
                  a=a.concat(arr[2][i]+" ")
               }
               console.log('a is' ,a)
            }
            let b=' '
            if(arr[3]!==null)
            {
            for(let i=0;i<arr[3].length;i++)
            {
               b=b.concat(arr[3][i]+" ")
            }
            console.log('b is' ,b)
         }
            return <AdminAccordian
            name={categoriesForAdmin.name}
            key={categoriesForAdmin.id}
            a={categoriesForAdmin.fieldName}
            b={categoriesForAdmin.values}
            cid={categoriesForAdmin.id}
            update={()=>updateCategory(categoriesForAdmin.id)}
            clicked={()=>handler(categoriesForAdmin.id)}
            />
        })}
        </>:null}

        </div>

    )
}
const mapStateToProps = state=>{
    console.log(state.category.categoriesForAdmin)
    return{
        categoriesForAdmin:state.category.categoriesForAdmin
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getAllCategoriesForAdmin()),
        onClicked1:()=>dispatch(actions.viewCategoryMetadataField),
        updateId:(id)=>dispatch(actions.updateId(id))
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(ViewAllCategoriesForAdmin)
