import React, { useState,useEffect } from 'react'
import Input from '../../components/Ui/Backdrop/Input/Input';
import classes from '../Login/login.module.css';
import {checkValidity,updateObject} from '../../shared/utility';
import * as actions from '../../store/actions/index';
import { connect } from 'react-redux';
const ForgotPassword = props => {

    const[authForm,setAuthForm] =  useState({
            email:{
                elementType:'input',
                elementConfig:{
                    type:'email',
                    placeholder:'your email'
                },
                value:'',
                validation:{
                    required:true,
                    isEmail: true
                },
                valid : false,
                touched:false
            }
        })

        console.log("hjefdhggeh",props.registerSuccessful)
        if(props.forgotPasswordSuccessful)
        {
            props.history.push("/")
        }

        const registerHandler=(event)=>{
            event.preventDefault();
            props.onRegister(authForm.email.value,authForm.firstName.value,authForm.lastName.value,authForm.password.value,authForm.confirmpassword.value,authForm.contactNumber.value);
        }

     const inputChangedHandler = (event,controlName)=>{
        const updatedControls = {
            ...authForm,
            [controlName]: {
                ...authForm[controlName],
                value: event.target.value,
                valid: checkValidity(event.target.value,authForm[controlName].validation),

                touched:true
            }
        };
        setAuthForm(updatedControls);
    }

        const formElementsArray=[];
        for(let key in authForm){
            formElementsArray.push({
                id:key,
                config:authForm[key]

            })
        }


        let form = formElementsArray.map(formElement=>(
            <Input
            key = {formElement.id}
            elementType={formElement.config.elementType}
            elementConfig={formElement.config.elementConfig}
            value={formElement.config.value}
            changed={(event)=>inputChangedHandler(event,formElement.id)}
            touched={formElement.config.touched}
            />
        ))

        return (
            <div className={classes.Auth}>
                <form onSubmit={registerHandler}>
                 {form}
                 <button className={classes.Button}>Send Email</button>
                </form>
            </div>
        )

        }


const mapStateToProps = state=>{
    return{
        error:state.register.error,
        forgotPasswordSuccessful:state.register.registerSuccessful
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: (email)=>dispatch(actions.forgotPassword(email))
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(ForgotPassword)
