import React,{useEffect} from 'react'
import {connect} from 'react-redux';
import * as actions from '../../../store/actions/index'
import ViewCategoriesAccordian from '../ViewCategoriesAccordian'
const ViewAllCategoriesForCustomer=(props)=> {
    useEffect(() => {
        props.productOfCategory(props.id)
      }, [props.productOfCategory(props.id)])


    // const {onClicked}=props
    // useEffect(()=>{
    //     onClicked()
    // },[onClicked])
    if(props.error!==null)
    {
        props.onClicked4(props.id)
    }

    const subCategoriesHandler=(id)=>{

        props.productOfCategory(id)
        props.onClicked1(id)
       props.onClicked3(id)
    }



    return (
        <div>
        {           
            props.customerCategories.map((customerCategories) => {
            return <ViewCategoriesAccordian
            name={customerCategories[0]}
            key={customerCategories[1]}j
            clicked={()=>subCategoriesHandler(customerCategories[1])}
            />
        })
        }
        <button onClick={props.onClicked2}>Go Back<span onClick={props.onClicked2} class="glyphicon glyphicon-step-backward"></span></button>

        </div>

    )
}
const mapStateToProps = state=>{
    console.log(state.category.id," id bhasad wali")
    return{
        error:state.category.error,
        customerCategories:state.category.customerCategories,
        id:state.category.id
    }
}

const mapDispatchToProps = dispatch =>{
    return{
        onClicked: ()=>dispatch(actions.getCategory()),
        onClicked1:(id)=>dispatch(actions.getSubCategoriesForCustomer(id)),
        onClicked2: ()=>dispatch(actions.getCategoriesForCustomer()),
        onClicked3:(id)=>dispatch(actions.updateId(id)),
        onClicked4:(id)=>dispatch(actions.getFilterCategoriesForCustomer(id)),
        productOfCategory: (id)=>dispatch(actions.getProductsForCustomer(id))
        }
}
export default connect(mapStateToProps,mapDispatchToProps)(ViewAllCategoriesForCustomer)
