import React,{useEffect} from 'react'
import classes from '../Admin/AdminDashboard/adminDashboard.module.css'
import {connect} from 'react-redux'
import * as actions from '../../store/actions/index'

const SellerAccount=(props)=> {
    const {getCategoryForSeller,getProductsForSeller}=props
    useEffect(()=>{
        getCategoryForSeller()
        getProductsForSeller()

    },[getCategoryForSeller,getProductsForSeller])

    const getCategoryHandler=()=>{
        props.history.push("/viewCategoriesForSeller")
    }

    const getProductsHandler=()=>{
        props.history.push("/viewAllProductsForSeller")
    }

    return (
        <center>
            <h1>Your Account</h1>
        <div className={classes.Card} onClick={getCategoryHandler}> 
        <div className={classes.Container}>
             <h3><b>Add Product</b></h3>
            </div>

      </div>
      <div className={classes.Card} onClick={getProductsHandler}> 
        <div className={classes.Container}>
             <h3><b>View Products and Update</b></h3>
            </div>

      </div>
      <div className={classes.Card} onClick={getProfileHandler}> 
        <div className={classes.Container}>
             <h3><b>My Profile</b></h3>
            </div>

      </div>

      </center>
    )
}

const mapDispatchToProps = dispatch =>{
    return{
        getCategoryForSeller: ()=>dispatch(actions.getCategory()),
        getProductsForSeller:()=>dispatch(actions.getAllProductsForSeller())
    }
}

export default connect(null,mapDispatchToProps)(SellerAccount)
